var bindEvents=function(){var e,t={mobileMenuButton:document.querySelector(".navicon"),mobileMenu:document.querySelector(".navigation"),mobileShowButtons:document.querySelectorAll(".facilitator-toggle"),scrollToAnchors:document.querySelectorAll(".navigation-menu-item a, .tool-facilitator, .tool-training")};for(t.mobileMenuButton.addEventListener("click",function(e){e.preventDefault(),t.mobileMenu.classList.contains("show")?t.mobileMenu.classList.remove("show"):t.mobileMenu.classList.add("show")}),e=0;e<t.mobileShowButtons.length;e++)t.mobileShowButtons[e].addEventListener("click",function(e){e.preventDefault(),this.parentElement.classList.add("show")})},scrollToSlug=function(){var e=window.location.href.split("/").filter(Boolean);if(e.length>2){var t=e.pop(),o=document.querySelector("#"+t).getBoundingClientRect().top;setTimeout(function(){document.body.scrollTop=o},10)}};document.addEventListener("DOMContentLoaded",function(){bindEvents()});